import React, {useEffect, useState} from 'react';
import {useInput} from "../hooks/input";
import useDebounce from "../hooks/debounce";
import axios from "../axios";
import {IAirport, ServerResponse} from "../models/models";



export default function AirportSearch() {
    const input = useInput()
    const [] = useState<IAirport[]>([])
    const debounced = useDebounce<string>(input.value)

    async function searchAirports() {
        const response = await axios.get<ServerResponse<IAirport>>(`airports`, {params: {search: debounced, count: 10}})
        console.log(response.data)
    }

    useEffect(() => {
        if (debounced.length > 3) {
            searchAirports()
        }
        console.log(debounced)
    }, [debounced])

    return (
        <div className="mb-4 relative">
            <input
                type='text'
                className="border py-2 px-4 mb-4 outline-0 w-full h-[42px]"
                placeholder="Search"
                {...input}
            />

            <ul className="list-none absolute left-0 right-0 h-[200px] top-[42px] shadow-md">

            </ul>
        </div>
    );
};
