import React, {useEffect, useState} from 'react';
import AirportSearch from "../components/AirportSearch";
import AirportFilter from "../components/AirportFilter";
import AirportCard from "../components/AirportCard";

import {fetchAirports} from "../store/actions/airportAction";
import {useAppDispatch, useAppSeletor} from "../hooks/redux";
import ReactPaginate from 'react-paginate';

const ITEMS_PER_PAGE = 50

export default function MainPage() {
    const dispatch = useAppDispatch()
    const [page] = useState(1)
    const {error, loading, airports, count} = useAppSeletor( state => state.airport)

    const pageCount = Math.ceil(count / ITEMS_PER_PAGE)

    const pageChangeHandler = ({selected}: {selected: number}) => {
        // console.log(event)
    }

    useEffect(() => {
        dispatch(fetchAirports(page, ITEMS_PER_PAGE))

    }, [dispatch, page])

    return (
        <div className="container mx-auto max-w-[760px] pt-5 ">

            <AirportSearch/>

            <AirportFilter/>

            {loading && <p className="text-center text-lg">Loading...</p>}
            {error && <p className="text-center text-lg text-red-600">{error}</p>}

            {
                airports.map(airport => <AirportCard airport={airport}/>)
            }

            <ReactPaginate
                breakLabel="..."
                nextLabel=">"
                onPageChange={pageChangeHandler}
                pageRangeDisplayed={3}
                pageCount={pageCount}
                previousLabel="<"

            />

        </div>
    );
};
