import React, {ChangeEvent, useState} from 'react';
import {useAppSeletor} from "../hooks/redux";
import {IFilter} from "../models/models";

export default function AirportFilter(){
    const {regions, countries, loading, types} = useAppSeletor(state => state.handbook)

    const changeHandler = (event: ChangeEvent<HTMLSelectElement>) => {
        setFilter(prevState => ({...prev, [event.target.name]: event.target.value}))
    }

    const [filter, setFilter] = useState<IFilter>({
        type: '',
        country: '',
        region: ''
    })

    if (loading) return <p className="text-center">Loading...</p>

    return (
        <div className="border py-2 px-4 mb-2">
            <span className="font-bold mr-2">Filter</span>

            <select name="types" className="mr-2 border py-1 px-2" defaultValue=""
                onChange={changeHandler}
            >
                <option value="" disabled>Type</option>
                {types.map(t => <option key={t}>{t}</option>)}
            </select>

            <select name="countries" className="mr-2 border py-1 px-2" defaultValue="" onChange={changeHandler}>
                <option value="" disabled>Country</option>
                {countries.map(c => <option key={c}>{c}</option>)}
            </select>

            <select name="regions" className="border py-1 px-2" defaultValue="" onChange={changeHandler}>
                <option value="" disabled>Region</option>
                {regions.map(r => <option key={r}>{r}</option>)}
            </select>
        </div>
    );
};
