import React, {FormEvent} from 'react';
import {useInput} from "../hooks/input";
import {useAppDispatch} from "../hooks/redux";
import {register} from "../store/actions/authActions";

export default function AuthPage() {
    const username = useInput('')
    const password = useInput('')
    const dispatch = useAppDispatch()

    const isFormValid = () => username.value && password.value

    const submitHandler = (event: FormEvent) => {
        event.preventDefault()

        if (isFormValid()){
            dispatch(register({username: username.value, password: password.value}))
        }
    }



    return (
        <form className="container mx-auto max-w-[500px] pt-8"
        onSubmit={submitHandler}
        >
            <div className="mb-2">
                <label htmlFor="username" className="block">Username</label>
                <input type="text" {...username} id="username" className="border py-1 px-2 w-full"/>
            </div>

            <div>
                <label htmlFor="password" className="block">Username</label>
                <input type="password" {...password} id="password" className="border py-1 px-2 w-full mb-2 max-w-50"/>

                <button className="px-2 py-4 bg-blue-700 border" type="submit">Register</button>
            </div>
        </form>
    );
};
